(0000000591, 0000000022, 0000000003, 0000000001, 'Login'),
(0000000591, 0000000023, 0000000001, 0000000001, ''),
(0000000591, 0000000025, 0000000003, 0000000001, '# Style `login`'),
(0000000592, 0000000022, 0000000003, 0000000001, 'Register'),
(0000000592, 0000000023, 0000000001, 0000000001, ''),
